import { React, Component } from "react";
import { BrowserRouter as Router, Route ,Link, Routes, useLocation } from "react-router-dom";
import Home from "./components/Home";
import Organizers from "./components/Organizers";
import Systems from "./components/Systems";
import TutorialFormat from "./components/TutorialFormat";
import $ from 'jquery';
import './components/component styles/Navbar.css'

export default class App extends Component {
  jQueryCode = () => {
    $('.nav-link').on('click', function() {
      $('.active-link').removeClass('active-link');
      $(this).addClass('active-link');
    });
  }

  onRouteChange = () => {
    let location = useLocation();
    React.useEffect(() => {
    }, [location])
    console.log(location)
  }

  componentDidMount(){
    this.jQueryCode()
  }
  render(){
    return (
      <div className="App">

        <Router>
          <div className="navbar-container">
            <ul>
              <li className="nav-link active-link">
                <Link to="/">Home</Link>
                <div className="underline"></div>
              </li>
              <li className="nav-link">
                <Link to="/systems">Systems</Link>
                <div className="underline"></div>
              </li>
              <li className="nav-link">
                <Link to="/tutorialformat">Tutorial Format (Half-Day)</Link>
                <div className="underline"></div>
              </li>
              <li className="nav-link">
                <Link to="/organizers">Organizers</Link>
                <div className="underline"></div>
              </li>
            </ul>
          </div>
  
          <Routes>
            <Route exact path="/" element={<Home/>}/>
            <Route path="/systems" element={<Systems/>}/>
            <Route path="/tutorialformat" element={<TutorialFormat/>}/>
            <Route path="/organizers" element={<Organizers/>}/>
          </Routes>
        </Router>



      </div>
    );
  }

}
